  if (cell.state === "flag") content = "ðŸš©";
  else if (cell.state === "mine") content = "ðŸ’£";
  else if (cell.state === "open" && cell.neighborMines > 0)
    content = cell.neighborMines;

  return (
    <button
      className={`${styles.cell} ${styles[cell.state]}`}
      onClick={onLeftClick}
      onContextMenu={onRightClick}
    >
      {content}
    </button>
  );
}
